import org.scalatest.FunSuite
import play.api.libs.json.Json

class DbActorTest extends FunSuite{


  test("parsing raw response from tx"){
    val test = DbActor.parseAccountTx(Json.parse(s))
    assert(test.size === 10)

    assert(test.head.account === "rUmHjB6nMVeTLLYh7Sh2VkRtkZYc5GBtaF")
    assert(test.head.ledgerIndex === 3940173)
    assert(test.head.date === 569490492)
    assert(test.head.humanDate.toString === "2018-01-17T07:48:12Z")
    assert(test.head.hash === "444CDCC74A7B2E5B2C671917C1E231FCF40BA5CE2A8BB08A0527ECF74C203128" )
    assert(test.head.txnType === "OfferCreate")
    assert(test.head.txnAcc === "rW3fQoNNgbMooTscWAG6S3oCscUtf6AQv")
    assert(test.head.transaction.contains("meta") && test.head.transaction.contains("tx") && test.head.transaction.contains("Node"))


  }

  test("parsing marker"){
    val test = DbActor.parseMarker(Json.parse(s))
    assert(test.isDefined)
    test.foreach(m =>{
      assert(m.seq === 0)
      assert(m.ledger === 3940172)
    }
    )

  }



